import { Touchable } from '@buffet/icon';
import React from 'react';
import styles from './ExpandCollapse.css';

interface Props {
  id: string;
  state: string;
  onClick(id: string): void;
  'data-testid'?: string;
}

export const ExpandCollapse: React.StatelessComponent<Props> = props => {
  let iconId = 'sort';

  if (props.state === 'expand') {
    iconId = 'chevronDown';
  }

  if (props.state === 'collapse') {
    iconId = 'chevronUp';
  }

  return (
    <Touchable
      className={styles.icon}
      onClick={() => props.onClick(props.id)}
      iconId={iconId}
      data-testid={props['data-testid']}
    />
  );
};
