export type State = 'checked' | 'partial' | 'unchecked';
export type ToggleCount = 'all' | 'selected' | 'hidden';

export interface ToggleAll {
  isVisible: boolean;
  label: string;
  toggleCount: string;
  showGlobalExpansionToggle?: boolean;
  state?: string;
}

export interface CheckboxItem {
  id: string;
  label: string;
  state?: string;
  isCollapsed?: boolean;
  options?: Array<CheckboxItem>;
  onToggleClick?(id: string): void;
}
