import React from 'react';
import { render } from 'react-testing-library';

import Window from './Window';
import { ModalType } from '../types';


test('Should render an Default Window', async () => {
  const result = render(
    <Window isClosing={false}>
      <p>test</p>
    </Window>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a closing Window', async () => {
  const result = render(
    <Window isClosing={true}>
      <p>test</p>
    </Window>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a Prompt Window', async () => {
  const result = render(
    <Window isClosing={false} type={ModalType.prompt}>
      <p>test</p>
    </Window>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a Fullscreen Window', async () => {
  const result = render(
    <Window isClosing={false} type={ModalType.fullscreen}>
      <p>test</p>
    </Window>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a Fullscreen Window with Auto-Height', async () => {
  const result = render(
    <Window isClosing={false} type={ModalType.fullscreen} fullscreenAutoHeight={true}>
      <p>test</p>
    </Window>
  );

  expect(result.container.firstChild).toMatchSnapshot();
});
