import React from 'react';
import { Panel } from './panel';
import { render } from 'react-testing-library';

describe('Panel', async () => {
  it('Should show the panel', async () => {
    const { getByText } = render(<Panel title={'My Microapp Panel'} />);
    expect(getByText('My Microapp Panel')).toBeTruthy();
  });

  it('Should show the panel without title', async () => {
    const { container } = render(<Panel title={''} />);
    expect(container.firstChild).toMatchSnapshot();
  });

  it('Should show the panel with headerContent', async () => {
    const { container } = render(<Panel title={'My Microapp Panel'} headerContent={<p>Test</p>} />);
    expect(container.firstChild).toMatchSnapshot();
  });

  it('Should show the panel with customHeaderStyle', async () => {
    const { container } = render(
      <Panel title={'My Microapp Panel'} headerContent={<p>Test</p>} customHeaderStyle={{ width: '100%' }} />
    );
    expect(container.firstChild).toMatchSnapshot();
  });
});
