import React from 'react';
import styles from './RadioButton.css';
import { Touchable } from '@buffet/icon';
import classNames from 'classnames/bind';

const cx = classNames.bind(styles);

export interface RadioButtonProps {
  checked?: boolean;
  disabled?: boolean;
  label: React.ReactNode;
  value: string;
  className?: string;
  onChange?(value: string): void;
}

export const RadioButton: React.StatelessComponent<RadioButtonProps> = props => {
  return (
    <Touchable
      className={cx(props.className, {
        icon__disabled: props.disabled
      })}
      iconClassName={cx(props.checked ? 'selected' : 'icon', {
        icon__disabled: props.disabled
      })}
      onClick={() => {
        if (props.onChange) {
          props.onChange(props.value);
        }
      }}
      iconId={props.checked ? 'radioButtonSelected' : 'radioButton'}
      text={props.label}
    />
  );
};
