import React from 'react';
import styles from './LoadingPanel.css';
import { injectIntl, InjectedIntlProps } from 'react-intl';

interface ViewProps {
  show?: boolean;
}

interface Props extends ViewProps, InjectedIntlProps {}

export class LoadingPanelView extends React.Component<Props> {
  render() {
    return (
      <div className={styles.loadingPanel}>
        <div
          className={styles.spinner}
          role="alert"
          aria-label={this.props.intl.formatMessage({ id: 'chrome.loading' })}
        />
      </div>
    );
  }
}

export const LoadingPanel = injectIntl<ViewProps>(LoadingPanelView);
