import * as IconModule from '@buffet/icon';
import classNames from 'classnames/bind';
import React from 'react';
import styles from './SlideNav.scss';

const cx = classNames.bind(styles);

interface ISlideNavProps {
  id: string;
  isVisible: boolean;
  next: {
    isDisabled: boolean;
    onClick(): void;
  };
  padding: {
    left: number;
    right: number;
    top: number;
  };
  prev: {
    isDisabled: boolean;
    onClick(): void;
  };
}

export const SlideNav = (props: ISlideNavProps) => {
  const Icon = IconModule.Icon;
  const inlineStyles: React.CSSProperties = {
    display: 'flex',
    justifyContent: 'flex-end',
    left: props.padding.left,
    position: 'absolute',
    top: props.padding.top,
    width: `calc(100% - ${props.padding.right + props.padding.left}px)`,
  };

  return (
    <div style={inlineStyles}>
      <button
        onClick={props.prev.onClick}
        className={cx('button')}
        disabled={props.prev.isDisabled}
        data-testid={`damper-slide-control--${props.id}--button_prev`}
      >
        <Icon iconId="chevronLeft" className={cx('icon')} />
      </button>
      <button
        onClick={props.next.onClick}
        className={cx('button')}
        disabled={props.next.isDisabled}
        data-testid={`damper-slide-control--${props.id}--button_next`}
      >
        <Icon iconId="chevronRight" className={cx('icon')} />
      </button>
    </div>
  );
}
