const getHighestNormalizedValue = (num: number, bracket: number = 50, bracketSize: number = 50): number => {
  const count = num / bracket;

  if (count > 1) {
    const nextBracket = bracket + bracketSize;
    return getHighestNormalizedValue(num, nextBracket);
  }

  return bracket;
};

/**
 * Brackets the provided number to a normalized value
 */
export const bracketNumberToNormalizedValue = (num: number) => {
  if (num <= 25) {
    const closestFive = num - (num % 5);
    return getHighestNormalizedValue(num, closestFive, 5);
  }

  if (num <= 100) {
    const closestTen = num - (num % 10);
    return getHighestNormalizedValue(num, closestTen, 10);
  }

  if (num <= 250) {
    const closestTwentyFive = num - (num % 25);
    return getHighestNormalizedValue(num, closestTwentyFive, 25);
  }

  if (num <= 1000) {
    const closestFifty = num - (num % 50);
    return getHighestNormalizedValue(num, closestFifty, 50);
  }

  const closestHundred = num - (num % 100);
  return getHighestNormalizedValue(num, closestHundred, 100);
}
