/**
 * Gets the highest area value of all area datapoints, includes stacked
 */

// @TODO: This should probably just be part of this file
import { getAreasMaxValues } from './getAreasMaxValues';
import { max } from './max';

import { ColumnType } from '../../@types/columns';

const getHighestAreaValue = (areas: any, columns: ColumnType): number => {
  if (areas) {
    const filteredAreas = getAreasMaxValues({ data: areas, columns });

    return max(filteredAreas.map((set) => {
      const maxVal = set.map((v) => v.maxVal);
      return max(maxVal);
    }));
  }

  return 0;
}

export { getHighestAreaValue };
