import React from 'react';

interface ICubeProps {
  key: React.ReactNode;
  label: string;
  cubeStyle: any;
  textStyle?: any;
  textOffsetY?: number;
}

const Cube = (props: ICubeProps) => {
  const defaultStyle = {
    height: 40,
    width: 40,
  }

  const defaultTextStyle = {
    fontSize: '18px',
    fontWeight: 'bold',
    lineHeight: '24px',
  };

  const cubeStyle = {
    ...defaultStyle,
    ...props.cubeStyle,
  };

  const textStyle = {
    ...defaultTextStyle,
    ...props.textStyle,
  }

  const textOffsetY = props.textOffsetY || 8;

  return (
    <g>
      <rect {...cubeStyle}/>
      <text
        x={cubeStyle.width / 2}
        y={(cubeStyle.height / 2) + textOffsetY}
        textAnchor="middle"
        style={textStyle}
      >
        {props.label}
      </text>
    </g>
  );
};

export { Cube };
