import React from 'react';
import { Link } from 'react-router-dom';

import { Group } from '../types';

interface GroupsResultProps {
  restaurants: Group[];
}

export function GroupsResult({ restaurants }: GroupsResultProps ) {
  return <ul className="group-result list">
    {restaurants.map(g => (
      <li key={g.gid}>
          <span className="id">{g.gid}</span>
          <span className="name">
            <a href={`/group/${g.gid}/home`} target="_blank">{g.name}</a>
          </span>
          <span className="action">
            <Link to={`/groups/${g.gid}/permissions`}>
              Permissions
            </Link>
            <a target="_blank" href={`/group/${g.gid}/manage-membership`}>
              Membership
            </a>
            <a target="_blank" href={`/group/${g.gid}/management`}>
              Management
            </a>
          </span>
      </li>
    ))}
  </ul>;
}
