import React, { SFC } from 'react';
import { formValues } from 'redux-form';
import { dayOrdering } from './constants';
import CheckIcon from './images/CheckIcon';

interface Props {
  [index: string]: any;
}

export const DayCheckmark: SFC<Props> = props => {
  const day = props.day && props.day.toLowerCase();
  const hasTimes = day && props[day] && Object.values(props[day]).indexOf(true) >= 0;

  return (
    <span className={`${hasTimes ? '' : 'hidden'}`}>
      <CheckIcon width="13" height="9" fill="#6CB6F5" />
    </span>
  );
};

export default formValues<any, any>(...dayOrdering)(DayCheckmark);
