export const validate = (values: any) => {
  const { name, restaurantLegalName, agreed } = values;
  const errors: any = {};
  if (!name) {
    errors.name = true;
  }
  if (!restaurantLegalName) {
    errors.restaurantLegalName = true;
  }
  if (!agreed) {
    errors.agreed = true;
  }
  return errors;
};
