import moment from 'moment';
import { dateFormat, DEFAULT_NUM_COVERS, DEFAULT_PRICE_PER_COVER, defaultSuppressionDates } from './constants';

const reducedDefaultSuppressionDates = Object.keys(defaultSuppressionDates).reduce(
  (accumulator, defaultDateId) => {
    accumulator[defaultDateId] = true;
    return accumulator;
  },
  {} as any,
);

const initialValues = {
  blockedDays: {
    custom: {},
    default: Object.assign({}, reducedDefaultSuppressionDates),
  },
  seatingDates: {
    startDate: moment()
      .add(1, 'day')
      .format(dateFormat.MOMENT),
  },
};

export default initialValues;
