import { shallow } from 'enzyme';
import * as React from 'react';
import promosFixture from '../../../../internals/fixtures/promosFixture';
import PromoCard from './PromoCard';

describe('<PromoCard />', () => {
  const promos = {
    Featured: {
      joined: promosFixture.promos[2],
      unjoined: promosFixture.promos[1],
    },
    Seasonal: {
      joined: promosFixture.promos[4],
      unjoined: promosFixture.promos[8],
    },
  };

  describe('snapshots', () => {
    ['Featured', 'Seasonal'].forEach(type => {
      ['unjoined', 'joined'].forEach(isJoined => {
        it(`should render as type '${type}' with isJoined '${isJoined}'`, () => {
          const promo = promos[type][isJoined];
          expect(shallow(<PromoCard promo={promo} />)).toMatchSnapshot();
        });
      });
    });
  });
});
