import React from 'react';
import ArtisanalCocktailIcon from './ArtisanalCocktailIcon';
import BrunchIcon from './BrunchIcon';
import BusinessBitesIcon from './BusinessBitesIcon';
import ByobIcon from './ByobIcon';
import ChristmasDayIcon from './ChristmasDayIcon';
import ChristmasEveIcon from './ChristmasEveIcon';
import CincoDeMayoIcon from './CincoDeMayoIcon';
import DefaultIcon from './DefaultIcon';
import EasterIcon from './EasterIcon';
import FathersDayIcon from './FathersDayIcon';
import GlutenFreeIcon from './GlutenFreeIcon';
import HannukahIcon from './HannukahIcon';
import MothersDayIcon from './MothersDayIcon';
import NewYearsIcon from './NewYearsIcon';
import OutdoorDiningIcon from './OutdoorDiningIcon';
import ThanksgivingIcon from './ThanksgivingIcon';
import TheaterMenuIcon from './TheaterMenuIcon';
import ValentinesDayIcon from './ValentinesDayIcon';
import VegetarianIcon from './VegetarianIcon';

interface Props {
  id: number;
}

export default function PromoIcon({ id }: Props) {
  switch (id) {
    case 1: // US
      return <ThanksgivingIcon />;
    case 4: // US
    case 583: // GB
      return <NewYearsIcon />;
    case 5: // US
    case 754: // GB
      return <ChristmasEveIcon />;
    case 6: // US
    case 584: // GB
      return <ChristmasDayIcon />;
    case 14: // US
    case 712: // GB
      return <OutdoorDiningIcon />;
    case 77: // US
    case 441: // GB
      return <MothersDayIcon />;
    case 85: // US
    case 787: // GB
      return <FathersDayIcon />;
    case 102: // US
    case 768: // GB
      return <ValentinesDayIcon />;
    case 212: // US
    case 598: // GB
      return <BrunchIcon />;
    case 228: // US, GB
      return <EasterIcon />;
    case 243: // US
    case 589: // GB
      return <TheaterMenuIcon />;
    case 355: // US
      return <CincoDeMayoIcon />;
    case 369: // US, GB
      return <ArtisanalCocktailIcon />;
    case 370: // US
      return <ByobIcon />;
    case 371: // US, GB
      return <BusinessBitesIcon />;
    case 630: // US
      return <GlutenFreeIcon />;
    case 631: // US
      return <VegetarianIcon />;
    case 728: // US
      return <HannukahIcon />;
    default:
      return <DefaultIcon />;
  }
}
