import React, { SFC } from 'react';
import { FormattedDate, FormattedTime } from 'react-intl';
import DetailLine, { DetailLineCommonProps } from './DetailLine';

interface ValueProps {
  date: Date;
}

type Props = ValueProps & DetailLineCommonProps;

const DateTime: SFC<Props> = ({ date, ...rest }) => (
  <DetailLine
    value={
      <>
        <FormattedTime value={date} hour="numeric" minute="numeric" hour12 />
        {', '}
        <FormattedDate value={date} month="short" day="numeric" year="numeric" />
      </>
    }
    {...rest}
  />
);

export default DateTime;
