import React, { Fragment, SFC } from 'react';

import { InjectedIntl } from 'react-intl';
import defaultSuppressionDates from '../../default-suppression-dates.json';
import renderSuppressionDates from '../helpers/render-suppression-dates';
import DetailLine, { DetailLineCommonProps } from './DetailLine';

interface ValueProps {
  suppressionDates: any;
  editListItem: JSX.Element | null;
}

interface SupportingProps {
  intl: InjectedIntl;
}

type Props = ValueProps & SupportingProps & DetailLineCommonProps;

const SuppressionDates: SFC<Props> = ({ suppressionDates, editListItem, intl, ...rest }) => (
  <DetailLine
    value={renderSuppressionDates(intl, suppressionDates, defaultSuppressionDates, editListItem)}
    {...rest}
    valueContainer={Fragment}
  />
);

export default SuppressionDates;
