import { currencySymbolMap, defaultCurrency } from '../../constants';

export function formatNumber(value: number): string {
  return new Intl.NumberFormat(navigator.language).format(value);
}

export function formatCurrency(value: number, currencyCode: string, rounded?: boolean): string {
  const digits = rounded ? 0 : 2;
  const supportedCurrency = currencySymbolMap[currencyCode] ? currencyCode : defaultCurrency;

  const currency = {
    currency: supportedCurrency,
    maximumFractionDigits: digits,
    minimumFractionDigits: digits,
    style: 'currency',
  };

  return new Intl.NumberFormat(navigator.language, currency).format(value);
}
