import { Chevron } from 'gca-react-components';

import classNames from 'classnames';
import React, { Component } from 'react';

import './CustomPromotionsIntroduction.scss';

import { FormattedMessage, InjectedIntlProps, injectIntl } from 'react-intl';

interface State {
  isOpen: boolean;
}

class CustomPromotionsIntroduction extends Component<{}, State> {
  public state = {
    isOpen: false,
  };

  public render() {
    const { isOpen } = this.state;
    return (
      <section className="custom-promotions-introduction" onClick={this.handleClick}>
        <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.header" tagName="h2" />
        <p>
          <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.description" />
          {!isOpen && <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.link" tagName="em" />}
        </p>
        <span className="toggle">
          <Chevron direction={isOpen ? 'up' : 'down'} />
        </span>
        <div
          className={classNames({
            active: isOpen,
            expandable: true,
          })}
          data-testid="customPromotionExpandableSection"
        >
          <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.images_description" tagName="p" />
          <ul>
            <li>
              <img
                height={160}
                width={160}
                /* tslint:disable-next-line:max-line-length */
                src="https://s3-us-west-2.amazonaws.com/otservices-promoted-inventory/flexible-pricing/Search%20Results.png"
              />
              <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.search_results" tagName="h3" />
              <FormattedMessage
                id="guestcampaigns.list.custom_promotions.introduction.search_results.description"
                tagName="p"
              />
            </li>
            <li>
              <img
                height={160}
                width={160}
                /* tslint:disable-next-line:max-line-length */
                src="https://s3-us-west-2.amazonaws.com/otservices-promoted-inventory/flexible-pricing/Lists%20on%20homepage.png"
              />
              <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.homepage" tagName="h3" />
              <FormattedMessage
                id="guestcampaigns.list.custom_promotions.introduction.homepage.description"
                tagName="p"
              />
            </li>
            <li>
              <img
                height={160}
                width={160}
                /* tslint:disable-next-line:max-line-length */
                src="https://s3-us-west-2.amazonaws.com/otservices-promoted-inventory/flexible-pricing/Marketing%20emails.png"
              />
              <FormattedMessage id="guestcampaigns.list.custom_promotions.introduction.promotions" tagName="h3" />
              <FormattedMessage
                id="guestcampaigns.list.custom_promotions.introduction.promotions.description"
                tagName="p"
              />
            </li>
          </ul>
        </div>
      </section>
    );
  }

  private handleClick = () => {
    const isOpen = !this.state.isOpen;
    this.setState({
      isOpen,
    });
  };
}

export default CustomPromotionsIntroduction;
export { CustomPromotionsIntroduction };
