import { ModalWrapper } from 'gca-react-components';
import React, { SFC } from 'react';
import { FormattedMessage, InjectedIntlProps, injectIntl } from 'react-intl';
import './ModalLearnMore.scss';

interface OP {
  onConfirm: () => void;
}

type Props = OP & InjectedIntlProps;

const ModalLearnMore: SFC<Props> = ({ intl: { formatMessage }, onConfirm }) => {
  return (
    <ModalWrapper
      confirmButtonText={formatMessage({ id: 'gifts.finished.ok_got_it' })}
      /* 'Cancel' (click outside modal) and 'Confirm' (click 'OK, Got it') both close the modal and that's it */
      onCancel={onConfirm}
      onConfirm={onConfirm}
      title={formatMessage({ id: 'flexiblepricing.educationpage.billdetail.flexible_pricing' })}
    >
      <article className="flexible-pricing-modal">
        <FormattedMessage id="flexiblepricing.modal.learnmore.why_are_we" tagName="h2" />
        <FormattedMessage id="flexiblepricing.modal.learnmore.why_are_we.description" tagName="p" />
        <FormattedMessage id="flexiblepricing.modal.learnmore.how_does_the" tagName="h2" />
        <FormattedMessage id="flexiblepricing.modal.learnmore.how_does_the.description" tagName="p" />
        <FormattedMessage id="flexiblepricing.modal.learnmore.why_do_i" tagName="h2" />
        <FormattedMessage id="flexiblepricing.modal.learnmore.why_do_i.description" tagName="p" />
      </article>
    </ModalWrapper>
  );
};

export default injectIntl(ModalLearnMore);
