import React, { SFC } from 'react';
import { Redirect, Route, RouteComponentProps, Switch } from 'react-router-dom';
import CreateSpecial from './Specials/CreateSpecial';
import SpecialsList from './Specials/SpecialsList';

export const enum Routes {
  List = 'list',
  Campaign = 'campaign',
}

type RP = RouteComponentProps<any>;

const Specials: SFC<RP> = ({ match }) => (
  <Switch>
    <Route path={`${match.url}/${Routes.List}`} component={SpecialsList} />
    <Route path={`${match.url}/${Routes.Campaign}`} component={CreateSpecial} />
    <Redirect to={`${match.url}/${Routes.List}`} />
  </Switch>
);

export default Specials;
