import React, { SFC } from 'react';
import { FormattedMessage } from 'react-intl';
import { formValues } from 'redux-form';

import { DisplaySpecialTypes } from '../../store/campaigns/campaigns.types';

interface Props {
  type?: DisplaySpecialTypes;
}

export const AlcoholWarning: SFC<Props> = ({ type }) => {
  const showWarning = type && [DisplaySpecialTypes.ComplimentaryDrink, DisplaySpecialTypes.HappyHour].includes(type);

  return showWarning ? (
    <li data-testid="alcohol-warning">
      <FormattedMessage id="guestcampaigns.specials.form.alcohol_warning" />
    </li>
  ) : null;
};

export default formValues<Props, 'type'>('type')(AlcoholWarning);
