import { reducer as formReducer } from 'redux-form';
import { reducerWithoutInitialState } from 'typescript-fsa-reducers';
import { FORM_NAME } from '../../PrivateDining/EnabledV2/EnabledForm';
import { postPrivateDining } from '../privatedining/privatedining';

const customFormReducer = formReducer.plugin({
  [FORM_NAME]: reducerWithoutInitialState().case(postPrivateDining.done, state => ({})),
});

export default customFormReducer;
