import React, { ReactNode } from 'react';
import './SimpleCard.scss';

interface Props {
  title: string;
  description: string;
  children?: ReactNode;
}

export function SimpleCard({ title, description, children }: Props) {
  return (
    <div className="simplecard">
      {children}
      <h2>
        {title}
      </h2>
      <p>
        {description}
      </p>
    </div>
  );
}
