import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';
import { PureEditReport as EditReport } from './EditReport';

describe('<EditReport /> Update', () => {
  it('should render an update edit', () => {
    const props = {
      editReportActiveUpdate: jest.fn(),
      isUpdating: false,
      report: {
        Frequency: 'Daily',
        Recipients: ['test@ot.com'],
        ReportId: 'test',
        RestaurantIds: [123],
        Type: 'Detailed',
      },
      rid: 123,
      updateReportRequest: jest.fn(),
      userEmail: 'foo@bar.com',
    };

    const wrapper = shallowWithIntl(<EditReport {...props} />);

    expect(wrapper).toMatchSnapshot();
  });
});

describe('<EditReport /> Create', () => {
  it('should render a create update ', () => {
    const props = {
      editReportActiveUpdate: jest.fn(),
      isCreate: true,
      isUpdating: false,
      report: { Frequency: '', Type: '', RestaurantIds: [], Recipients: [] },
      rid: 123,
      updateReportRequest: jest.fn(),
      userEmail: 'foo@bar.com',
    };

    const wrapper = shallowWithIntl(<EditReport {...props} />);

    expect(wrapper).toMatchSnapshot();
  });
});
