import { Nav, ReportsIcon, StarIcon } from 'gca-react-components';
import React from 'react';
import { FormattedMessage } from 'react-intl';
import { NavLink } from 'react-router-dom';

function ReviewsNav() {
  return (
    <Nav>
      <NavLink to="/reviews" activeClassName="active">
        <StarIcon /> <FormattedMessage id="nav.reviews" />
      </NavLink>
      <NavLink to="/reports" activeClassName="active">
        <ReportsIcon /> <FormattedMessage id="nav.reports" />
      </NavLink>
    </Nav>
  );
}

export default ReviewsNav;
