import { shallowWithIntl } from 'gca-jest-config';
import React from 'react';
import { PureModalManager as ModalManager } from './ModalManager';

describe('<ModalManager />', () => {
  let wrapper;
  let props;
  beforeEach(() => {
    props = {
      closeModal: jest.fn(),
      confirmCloseModal: jest.fn(),
    };

    wrapper = shallowWithIntl(<ModalManager {...props} />);
  });

  it('should render null when no currentModal is set', () => {
    expect(wrapper).toMatchSnapshot();
  });

  it('should render the DeleteReportModal', () => {
    wrapper.setProps({ currentModal: 'DELETE_REPORT' });
    expect(wrapper).toMatchSnapshot();
  });

  it('should render the ErrorModal', () => {
    wrapper.setProps({ currentModal: 'ERROR' });
    expect(wrapper).toMatchSnapshot();
  });
});
