import includes from 'lodash/includes';
import isString from 'lodash/isString';
import startsWith from 'lodash/startsWith';

export function isEnum(values: any[]) {
  return (value: any) => includes(values, value);
}

export function isISO(value: string) {
  let ISO;

  try {
    const date = new Date(value);
    ISO = date.toISOString();
  } catch (e) {
    ISO = '';
  }

  return startsWith(ISO, value);
}

export function isDateString(str: string) {
  return isString(str) && str.length >= 2 && (str.match(/-/g) || []).length === 2;
}
