import { Button } from '@buffet/button';
import React from 'react';
import { FormattedMessage } from 'react-intl';

import IntroHeader from '../../common/IntroHeader/IntroHeader';
import { eventTracker, trackingCategory, trackingEvents } from '../../helpers/tracking';
import './ActivePage.scss';

const trackLink = () => {
  eventTracker.sendEvent({
    action: trackingEvents.activePage.action,
    category: trackingCategory,
    label: trackingEvents.activePage.labels.launchToVenga,
  });
};

const ActivePage = () => {
  return (
    <div className="venga-page active-page">
      <section className="page-content">
        <div className="intro-wrapper">
          <IntroHeader>
            <Button type="a" href="https://dashboard.getvenga.com" target="_blank" theme="primary" onClick={trackLink}>
              <FormattedMessage tagName="span" id="venga.launch.button" />
            </Button>
          </IntroHeader>
        </div>
      </section>
    </div>
  );
};

export default ActivePage;
