import { Button } from '@buffet/button';
import React from 'react';
import { FormattedMessage } from 'react-intl';
import { smoothScrollTo } from 'shared-utils/scrollTo/scrollTo';

import IntroHeader from '../../common/IntroHeader/IntroHeader';
import { eventTracker, trackingCategory, trackingEvents } from '../../helpers/tracking';

const scrollToPricing = () => {
  smoothScrollTo('#section_pricing', { target: '.venga-page' });

  eventTracker.sendEvent({
    action: trackingEvents.marketingPage.action,
    category: trackingCategory,
    label: trackingEvents.marketingPage.labels.getVengaNow,
  });
};

export default () => (
  <section className="section intro">
    <IntroHeader>
      <Button type="button" theme="primary" onClick={scrollToPricing} data-testid="ctaButton">
        <FormattedMessage tagName="span" id="venga.marketing.intro.ctaButton" />
      </Button>
    </IntroHeader>
  </section>
);
