import React from 'react';
import PropTypes from 'prop-types';

import './BusyIcon.scss';

export function BusyIcon(props) {
  const { size, type } = props;
  return <i style={{ height: size, width: size }} className={`gca-busy-icon busy ${type}`} />;
}

BusyIcon.propTypes = {
  size: PropTypes.string,
  type: PropTypes.oneOf(['primary', 'confirm', 'publish', 'default', 'danger', 'light']),
};

BusyIcon.defaultProps = {
  size: '20',
  type: 'primary',
};
