import { shallow } from 'enzyme';
import React from 'react';
import { ModalWrapper } from './ModalWrapper';

describe('<ModalWrapper />', () => {
  it('should render the same as the snapshot', () => {
    const confirmClickSpy = jest.fn();
    const cancelClickSpy = jest.fn();
    const wrapper = shallow(
      <ModalWrapper
        onConfirm={confirmClickSpy}
        onCancel={cancelClickSpy}
        title="Test Modal"
        cancelButtonText="Cancel"
        confirmButtonText="Confirm"
        includeCancelButton
      >
        <p>This is the first line of a test modal.</p>
        <p>This is the second line of a test modal.</p>
      </ModalWrapper>,
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render without a cancel button', () => {
    const confirmClickSpy = jest.fn();
    const cancelClickSpy = jest.fn();
    const wrapper = shallow(
      <ModalWrapper
        onConfirm={confirmClickSpy}
        onCancel={cancelClickSpy}
        title="Test Modal"
        confirmButtonText="Confirm"
      >
        <p>This is the first line of a test modal.</p>
        <p>This is the second line of a test modal.</p>
      </ModalWrapper>,
    );
    expect(wrapper).toMatchSnapshot();
  });
});
