import { shallow } from 'enzyme';
import React from 'react';

import { Panel } from './Panel';

describe('<Panel />', () => {
  it('default snapshot', () => {
    const props = {
      title: 'Title',
      headerContent: <div>Header Content</div>,
    };

    const wrapper = shallow(
      <Panel {...props}>
        <div>content</div>
      </Panel>,
    );

    expect(wrapper).toMatchSnapshot();
  });

  it('minimal snapshot', () => {
    const props = {};

    const wrapper = shallow(<Panel {...props} />);

    expect(wrapper).toMatchSnapshot();
  });
});
