import React from 'react';
import ProfileHours from './ProfileHours';

/* eslint-disable react/prop-types */
export function ProfileHoursAdaptor(props) {
  const { input, meta, ...custom } = props;
  const valid = meta.valid || meta.pristine;
  return (
    <ProfileHours
      {...custom}
      errors={meta.error || []}
      valid={valid}
      onChange={input.onChange}
      shifts={input.value}
      disabled={props.disabled}
    />
  );
}

export default ProfileHoursAdaptor;
