import React from 'react';
import { BaseFieldProps } from 'redux-form';

type CustomFieldProps<ComponentProps> = BaseFieldProps & Partial<ComponentProps> & {
  showValidationWhenPristine?: boolean;
}

export class CustomField<ComponentProps> extends React.Component<CustomFieldProps<ComponentProps>> {}

export function customField<ComponentProps>(config: { component: ComponentProps }): CustomField<ComponentProps>;

export default CustomField;
