import { shallow } from 'enzyme';
import * as React from 'react';

import { DateInputFinalField } from './DateInputFinalField';

describe('<DateInputFinalField />', () => {

  it('should render the basic field', () => {
    expect(
      shallow(
        <DateInputFinalField
          name="myinput"
          invalidMessage="test"
        />)
      ).toMatchSnapshot();
  });

  it('should render the field as invalid', () => {
    expect(
      shallow(
        <DateInputFinalField
          name="myinput"
          invalidMessage="test"
          valid={false}
        />)
      ).toMatchSnapshot();
  });
});
