
import * as React from 'react';
import * as moment from 'moment';
import { customFinalField } from './customfield';

import Input, { Props as InputProps } from '../components/Input/Input';

/**
 * InputField react-final-form field for gca-react-components Input
 */
export const InputFinalField = customFinalField<InputProps>(
  ( { autoFocus, disabled, errorText, placeholder, valid, tabIndex }, {input} ) => {
    return (
      <Input
        type="text"
        autoFocus={autoFocus}
        disabled={disabled}
        tabIndex={tabIndex}
        errorText={errorText}
        valid={valid}
        placeholder={placeholder}
        {...input}
      />
    );
  }
);

/** @component */
export default InputFinalField;
