import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import ProfileHours, { Props as ProfileHoursProps } from '../components/ProfileHours/ProfileHours';

/**
 *
 */
export const ProfileHoursFinalField = customFinalField<ProfileHoursProps>(
  ( { countryCode, disabled, labels, locale, errors, standardShifts, timeRangeSeparator, valid } , { input } ) => {
    return (
      <ProfileHours
        timeRangeSeparator={timeRangeSeparator}
        standardShifts={standardShifts}
        shifts={input.value || []}
        valid={valid}
        labels={labels}
        errors={errors}
        disabled={disabled}
        locale={locale}
        countryCode={countryCode}
        onChange={ (shifts) => {
          input.onChange(shifts);
        }}
        onShiftsChange={ (shifts) => {
          input.onChange(shifts);
        }}
      />
    );
  }
);

/** @component */
export default ProfileHoursFinalField;