import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import SimpleRadioButtonGroup, { Props as SimpleRadioButtonGroupProps } from '../components/RadioButton/SimpleRadioButtonGroup';

/**
 *
 */
export const SimpleRadioButtonGroupFinalField = customFinalField<SimpleRadioButtonGroupProps>(
  ( { children, disabled }, { input } ) => {
    return (
      <SimpleRadioButtonGroup
        selectedValue={input.value}
        disabled={disabled}
        onChange={input.onChange}
      >
        {children}
      </SimpleRadioButtonGroup>
    );
  }
);

/** @component */
export default SimpleRadioButtonGroupFinalField;
