import * as React from 'react';
import * as moment from 'moment';

import { customFinalField } from './customfield';

import TextArea, { Props as TextAreaProps } from '../components/TextArea/TextArea';

/**
 * TextAreaField react-final-form field for gca-react-components TextArea
 */
export const TextAreaFinalField = customFinalField<TextAreaProps>(
  ( { disabled, formatLabel, labels, min, max, showLabels, valid }, { input } ) => {
    return (
      <TextArea
        formatLabel={formatLabel}
        labels={labels}
        disabled={disabled}
        value={input.value}
        onChange={input.onChange}
        max={max}
        min={min}
        showLabels={showLabels}
        valid={valid}
      />
    );
  }
);

/** @component */
export default TextAreaFinalField;
