import React from 'react';
import PropTypes from 'prop-types';
import dimensionWrapper from './dimensionWrapper';

const rotationMap = {
  up: 90,
  down: 270,
  right: 180,
  left: 0,
};

export const Arrow = dimensionWrapper(({ width, height, direction, color }) => {
  const rotation = rotationMap[direction];

  return (
    <svg width={width} height={height} viewBox="0 0 14 9" version="1.1" xmlns="http://www.w3.org/2000/svg">
      <title>{direction} arrow</title>
      <polygon
        fill={color}
        points="5 4 14 4 14 5 5 5 5 9 0 4.5 5 0 5 4"
        transform={`rotate(${rotation} 7 4.5)`}
      />
    </svg>
  );
});

Arrow.propTypes = {
  color: PropTypes.string,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
  height: PropTypes.string,
  width: PropTypes.string,
};

Arrow.defaultProps = {
  direction: 'left',
};
