export const enum LangLocale {
  AustralianEnglish = 'en-au',
  AmericanEnglish = 'en-us',
  BritishEnglish = 'en-gb',
  CanadianFrench = 'fr-ca',
  Dutch = 'nl-nl',
  FrenchFrench = 'fr-fr',
  GermanGerman = 'de-de',
  Italian = 'it-it',
  Japanese = 'ja-jp',
  MexicanSpanish = 'es-mx',
  SpanishSpanish = 'es-es',
}

export interface LanguageProvider {
  locale: LangLocale;
}

export function changeLocale(languageLocale: LangLocale): void

interface Selectors {
  locale: (state: any) => LangLocale;
}

export const selectors: Selectors;

export const initialState: LanguageProvider;

export function languageProviderReducer(state: LanguageProvider, action: any): LanguageProvider

export  function createReducer(state?: LanguageProvider): (state: LangLocale, action: any) => LanguageProvider
