import React from 'react';

import { InjectedIntlProps, injectIntl } from 'react-intl';
import {
  Permission,
} from '../store/types';


interface PermissionsListOwnProps {
  permissions: Permission[];
}

type PermissionsListProps = PermissionsListOwnProps & InjectedIntlProps;

const PermissionsList: React.SFC<PermissionsListProps> = ({ intl, permissions }) => (
  <span>
    {permissions
      .map((permUrn: Permission) => intl.formatMessage({ id: permUrn }))
      .join(', ')
    }
  </span>
);

export default injectIntl(PermissionsList);
