import React from 'react';

/**
 * Utility that insert a separator element in-between elements in a given array
 */
export default ( spans: React.ReactNode[], sep = ',') => {
  return spans.slice(1).reduce(
    (acc, el: React.ReactNode, idx) => (
      [ ...acc,
        <span key={`${idx}`}>{sep}</span>,
        el,
      ]),
      spans[0],
  );
}
