declare module '*.css' {
  const styles: any;
  export default styles;
}

interface SpriteSymbol {
  id: string;
  viewBox: string;
  content: string;
}

/**
 * Needed in order to load images in TypeScript.
 * @see https://github.com/Microsoft/TypeScript-React-Starter/issues/12#issuecomment-326370098
 */

declare module '*.png';
declare module '*.svg';
declare module '*.gif';
declare function require(name: string): any;

