import React from 'react';
import { render } from 'react-testing-library';
import { BusyIcon } from './BusyIcon';

test('Should render a default large busy icon', async () => {
  const result = render(<BusyIcon>simple button</BusyIcon>);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a small busy icon', async () => {
  const result = render(<BusyIcon size="small">simple button</BusyIcon>);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a default large busy icon', async () => {
  const result = render(<BusyIcon type="default">simple button</BusyIcon>);
  expect(result.container.firstChild).toMatchSnapshot();
});

test('Should render a default large busy icon', async () => {
  const result = render(<BusyIcon color="white">simple button</BusyIcon>);
  expect(result.container.firstChild).toMatchSnapshot();
});
