import React from 'react';

import classNames from 'classnames/bind';
import styles from './BusyIcon.css';

const cx = classNames.bind(styles);

interface Props {
  color?: string;
  className?: string;
  size?: 'large' | 'small' | 'xsmall';
  type?: 'bare' | 'primary' | 'default' | 'publish' | 'hollow' | 'link';
}

export const BusyIcon: React.SFC<Props> = props => {
  const { size, type, className } = props;
  return <i className={cx('busyIcon', type, 'busy', className, `busyIcon-${size}`)} />;
};

BusyIcon.defaultProps = {
  size: 'large',
  type: 'primary'
};
