interface InitState {
  status: 'init';
  data: undefined;
}

interface StartedState {
  status: 'started';
  data: undefined;
}

interface DoneState<T> {
  status: 'done';
  data: T;
}

interface FailedState {
  status: 'failed';
  data: undefined;
}

export type AsyncState<T> = InitState | StartedState | DoneState<T> | FailedState;
