import React from 'react';
import { render } from 'react-testing-library';
import { LabelGroup } from './labelgroup';

async function renderDefaultScenario() {
  const result = render(
    <LabelGroup label="Field One">
      <input id="foos" />
    </LabelGroup>
  );
  return result;
}

test('Should render a basic LabelGroup', async () => {
  const { container } = render(
    <LabelGroup label="Field One">
      <input id="foos" />
    </LabelGroup>
  );
  expect(container.firstChild).toMatchSnapshot();
});

test('Should use name as foo if id is not present', async () => {
  const { container } = render(
    <LabelGroup label="Field One">
      <input name="foo" />
    </LabelGroup>
  );
  expect(container.firstChild).toMatchSnapshot();
});

test('it should render also child that are not input element', async () => {
  const { container } = render(
    <LabelGroup label="Field One">
      <span>This is not an input</span>
    </LabelGroup>
  );
  expect(container.firstChild).toMatchSnapshot();
});
